function render(t){return t("div",{},[t("h3","countdown for simpleStore"),t("button",{domProps:{innerHTML:"-"},on:{click:this.handleDecrement}}),t("input",{attrs:{value:this.count},on:{input:this.handleUpdate},style:{width:"50px"}}),t("button",{domProps:{innerHTML:"+"},on:{click:this.handleIncrement}})])}Vue.use(SimpleStore);const store=new SimpleStore.Store({state:{count:0},getters:{doubleCount:t=>2*t.count},mutations:{updateCount(t,e){t.count=e}},actions:{increment({commit:t,state:e}){t("updateCount",++e.count)},decrement({commit:t,state:e}){t("updateCount",--e.count)}}});new Vue({el:"#app",store,render,computed:{count(){return this.$store.state.count},doubleCount(){return this.$store.getters.doubleCount}},created(){console.log(this.$store)},methods:{handleIncrement(){this.$store.dispatch("increment")},handleDecrement(){this.$store.dispatch("decrement")},handleUpdate(t){const e=Number(t.target.value);this.$store.commit("updateCount",e)}}});